INSERT INTO `kongfz` VALUES ('60', '郑织文', '九五品', '1000.00', '梦涛居', '今日上书', 'http://www.kfzimg.com/sw/kfzimg/599/7474e05e6d3d8aa2_MASK_b.jpg', 'http://www.kfzimg.com/sw/kfzimg/599/7474e086d588118a_MASK_b.jpg', 'http://www.kfzimg.com/sw/kfzimg/599/7474e072dd289e4e_MASK_b.jpg', 'http://www.kfzimg.com/sw/kfzimg/598/7474e14eb213b546_MASK_b.jpg', 'http://www.kfzimg.com/sw/kfzimg/598/7474e1768b212ee3_MASK_b.jpg', '云南昆明西山', '1', '郑织文', '郑织文', '1993-12', NULL, NULL, NULL, NULL, '1页', '2019-12-02', '\r\n                    郑织文、1923年10月出生，河南省鄢陵人。1935年在开封“省立女子中学”参加抗日救亡学生运动。1938年加入中华民族解放先锋队。1939年参加“战地教育工作团”，开展抗日宣传活动；4月奔赴延安，5月进入中国人民抗日军政大学第五期女生队学习；同年10月分配到八路军120师358旅政治部组织科任干事，11月加入中国共产党。1943年，调任120师独立第1旅政治部组织科干事。参加了晋西北反“扫荡”、百团大战和大生产运动。1946年，任晋绥军政干部学校组织科副科长。1950年任西南军区空军预科总队女生队政治指导员。1952年任云南省开远市中共滇南工委研究员。1954年任中共云南省委边委干部处副处长、省委组织部直工部干部处副处长、省委直工部宣传部副部长。\r\n                ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
